 54→                    case .newShare:
 55→                        newShareSettingsView
 56→                    case .manageShares:
 57→                        manageSharesView
 58→                    case .error(let message):
 59→                        errorView(message)
 60→                    }
 61→                }
 62→                .padding()
 63→            }
 64→            .background(Color.vaultBackground.ignoresSafeArea())
 65→            .navigationTitle("Share Vault")
 66→            .navigationBarTitleDisplayMode(.inline)
 67→            .toolbar {
 68→                ToolbarItem(placement: .cancellationAction) {
 69→                    Button("Close") {
 70→                        dismiss()
 71→                    }
 72→                    .accessibilityIdentifier("share_close")
 73→                }
 74→            }
 75→        }
 76→        .sheet(item: Binding(
 77→            get: { activePhrase.map(SharePhraseSheetItem.init(phrase:)) },
 78→            set: { activePhrase = $0?.phrase }
 79→        )) { item in
 80→            phraseSheet(phrase: item.phrase)
 81→                .presentationDetents([.medium, .large])
 82→        }
 83→        .task {
 84→            await initialize()
 85→        }
 86→        .onReceive(NotificationCenter.default.publisher(for: .CKAccountChanged)) { _ in
 87→            Task { await initialize() }
 88→        }
 89→        .task {
 90→            while !Task.isCancelled {
 91→                try? await Task.sleep(for: .seconds(1))
 92→                await refreshUploadJobs(reloadShares: false)
 93→            }
 94→        }
 95→        .onChange(of: ShareSyncManager.shared.syncStatus) { _, newStatus in
 96→            if case .upToDate = newStatus {
 97→                Task { await reloadActiveShares() }
 98→            }
 99→        }
100→        .onAppear {
101→            isShareScreenVisible = true
102→            applyIdleTimerPolicy()
103→        }
104→        .onDisappear {
105→            isShareScreenVisible = false
106→            releaseIdleTimerPolicyIfNeeded()
107→        }
108→    }
