110→    // MARK: - Initialization
111→
112→    private func initialize() async {
113→        guard let key = appState.currentVaultKey else {
114→            mode = .error("No vault key available")
115→            return
116→        }
117→
118→        let generation = initializationGeneration &+ 1
119→        initializationGeneration = generation
120→        currentOwnerFingerprint = KeyDerivation.keyFingerprint(from: key.rawBytes)
121→
122→        do {
123→            async let accountStatusTask = CloudKitSharingManager.shared.checkiCloudStatus()
124→
125→            let localSnapshot = try await Self.loadLocalSnapshot(vaultKey: key)
126→            guard generation == initializationGeneration else { return }
127→
128→            estimatedUploadSize = localSnapshot.estimatedUploadSize
129→            activeShares = localSnapshot.activeShares
130→
131→            await refreshUploadJobs(reloadShares: false)
132→            guard generation == initializationGeneration else { return }
133→            updateModeForCurrentData()
134→
135→            Task {
136→                await reconcileConsumedShares(
137→                    vaultKey: key,
138→                    initialShares: localSnapshot.activeShares,
139→                    generation: generation
140→                )
141→            }
142→
143→            let status = await accountStatusTask
144→            guard generation == initializationGeneration else { return }
145→            guard status == .available || status == .temporarilyUnavailable else {
146→                mode = .iCloudUnavailable(status)
147→                return
148→            }
149→        } catch {
150→            mode = .error(error.localizedDescription)
151→        }
152→    }
153→
154→    private func refreshUploadJobs(reloadShares: Bool = true) async {
155→        let previousRunningCount = runningUploadCount
156→        let latestJobs = ShareUploadManager.shared.jobs(forOwnerFingerprint: currentOwnerFingerprint)
157→        uploadJobs = latestJobs.filter(Self.shouldDisplayUploadJob)
158→        let shouldReloadShares = reloadShares || (previousRunningCount > 0 && runningUploadCount == 0)
159→        if shouldReloadShares {
160→            await reloadActiveShares()
161→        }
162→        applyIdleTimerPolicy()
163→        updateModeForCurrentData()
164→    }
165→
166→    private func updateModeForCurrentData() {
167→        mode = Self.resolveMode(
168→            currentMode: mode,
169→            hasShareData: !uploadJobs.isEmpty || !activeShares.isEmpty
170→        )
171→    }
172→
173→    static func resolveMode(currentMode: ViewMode, hasShareData: Bool) -> ViewMode {
174→        switch currentMode {
175→        case .loading:
176→            return hasShareData ? .manageShares : .newShare
177→        case .manageShares:
178→            return hasShareData ? .manageShares : .newShare
179→        case .newShare:
180→            // Keep user in manual "share with someone new" flow even while
181→            // uploads/shares are changing in the background.
182→            return .newShare
183→        case .iCloudUnavailable, .error:
184→            return currentMode
185→        }
186→    }
