684→    private func reloadActiveShares() async {
685→        guard let key = appState.currentVaultKey,
686→              let shares = try? await Self.loadActiveShares(vaultKey: key) else {
687→            activeShares = []
688→            return
689→        }
690→        // Sort by creation date, newest first
691→        activeShares = shares.sorted { $0.createdAt > $1.createdAt }
692→        updateModeForCurrentData()
