695→    /// Formats a date as relative time (e.g., "5 minutes ago", "1 day ago")
696→    private func relativeTimeString(from date: Date) -> String {
697→        let now = Date()
698→        let diff = now.timeIntervalSince(date)
699→        
700→        // Less than a minute
701→        if diff < 60 {
702→            return "just now"
703→        }
704→        // Less than an hour - show minutes
705→        else if diff < 3600 {
706→            let minutes = Int(diff / 60)
707→            return minutes == 1 ? "1 minute ago" : "\(minutes) minutes ago"
708→        }
709→        // Less than a day - show hours
710→        else if diff < 86400 {
711→            let hours = Int(diff / 3600)
712→            return hours == 1 ? "1 hour ago" : "\(hours) hours ago"
713→        }
714→        // Less than 30 days - show days
715→        else if diff < 2592000 {
716→            let days = Int(diff / 86400)
717→            return days == 1 ? "1 day ago" : "\(days) days ago"
718→        }
719→        // More than 30 days - show the date
720→        else {
721→            let formatter = DateFormatter()
722→            formatter.dateStyle = .medium
723→            formatter.timeStyle = .none
724→            return formatter.string(from: date)
725→        }
726→    }
