728→    private func revokeShare(_ share: VaultStorage.ShareRecord) async {
729→        guard let key = appState.currentVaultKey else { return }
730→
731→        // Update local state first for instant UI response
732→        activeShares.removeAll { $0.id == share.id }
733→        
734→        // Check if this was the last share - if so, terminate any ongoing sync
735→        let remainingShares = activeShares
736→        let remainingJobs = uploadJobs.filter { $0.shareVaultId != share.id }
737→        
738→        if remainingShares.isEmpty && remainingJobs.isEmpty {
739→            // No more users - terminate any ongoing uploads for this specific share
740→            for job in uploadJobs where job.shareVaultId == share.id && job.canTerminate {
741→                ShareUploadManager.shared.terminateUpload(
742→                    jobId: job.id,
743→                    vaultKey: key,
744→                    cleanupRemote: true
745→                )
746→            }
747→            uploadJobs.removeAll { $0.shareVaultId == share.id }
748→            mode = .newShare
749→        }
750→        // If there are remaining shares, sync will continue for those users
751→
752→        do {
753→            // Persist to index
754→            var index = try VaultStorage.shared.loadIndex(with: key)
755→            index.activeShares?.removeAll { $0.id == share.id }
756→            try VaultStorage.shared.saveIndex(index, with: key)
757→        } catch {
758→            shareVaultLogger.error("Failed to update index after revoke: \(error.localizedDescription, privacy: .public)")
759→        }
760→
761→        // Delete from CloudKit in background (fire-and-forget)
762→        Task {
763→            try? await CloudKitSharingManager.shared.revokeShare(shareVaultId: share.id)
764→        }
765→    }
766→
767→    private func stopAllSharing() async {
768→        guard let key = appState.currentVaultKey else { return }
769→
770→        isStopping = true
771→
772→        do {
773→            // Cancel all upload jobs for this vault first
774→            for job in uploadJobs where job.canTerminate {
775→                ShareUploadManager.shared.terminateUpload(
776→                    jobId: job.id,
777→                    vaultKey: key,
778→                    cleanupRemote: true
779→                )
780→            }
781→
782→            // Immediately clear local shares so UI feels responsive
783→            var index = try VaultStorage.shared.loadIndex(with: key)
784→            let sharesToDelete = index.activeShares ?? []
785→            index.activeShares = nil
786→            try VaultStorage.shared.saveIndex(index, with: key)
787→
788→            activeShares = []
789→            uploadJobs = []
790→            mode = .newShare
791→            isStopping = false
792→
793→            // Delete from CloudKit in background
794→            for share in sharesToDelete {
795→                try? await CloudKitSharingManager.shared.deleteSharedVault(shareVaultId: share.id)
796→            }
797→        } catch {
798→            isStopping = false
799→            mode = .error("Failed to stop sharing: \(error.localizedDescription)")
800→        }
801→    }
