803→    private func terminateUpload(_ job: ShareUploadManager.UploadJob) {
804→        ShareUploadManager.shared.terminateUpload(
805→            jobId: job.id,
806→            vaultKey: appState.currentVaultKey,
807→            cleanupRemote: true
808→        )
809→
810→        uploadJobs.removeAll { $0.id == job.id }
811→        activeShares.removeAll { $0.id == job.shareVaultId }
812→        if uploadJobs.isEmpty && activeShares.isEmpty {
813→            mode = .newShare
814→        }
815→    }
