849→    private struct LocalSnapshot: Sendable {
850→        let estimatedUploadSize: Int
851→        let activeShares: [VaultStorage.ShareRecord]
852→    }
853→
854→    private static func loadLocalSnapshot(vaultKey: VaultKey) async throws -> LocalSnapshot {
855→        try await Task.detached(priority: .userInitiated) {
856→            let index = try VaultStorage.shared.loadIndex(with: vaultKey)
857→            let estimatedSize = index.files.filter { !$0.isDeleted }.reduce(0) { $0 + $1.size }
858→            let shares = index.activeShares ?? []
859→            // Sort by creation date, newest first (descending order)
860→            let sortedShares = shares.sorted { $0.createdAt > $1.createdAt }
861→            return LocalSnapshot(
862→                estimatedUploadSize: estimatedSize,
863→                activeShares: sortedShares
864→            )
865→        }.value
866→    }
867→
868→    private static func loadActiveShares(vaultKey: VaultKey) async throws -> [VaultStorage.ShareRecord] {
869→        try await Task.detached(priority: .utility) {
870→            let index = try VaultStorage.shared.loadIndex(with: vaultKey)
871→            let shares = index.activeShares ?? []
872→            // Sort by creation date, newest first
873→            return shares.sorted { $0.createdAt > $1.createdAt }
874→        }.value
875→    }
876→
877→    private func reconcileConsumedShares(
878→        vaultKey: VaultKey,
879→        initialShares: [VaultStorage.ShareRecord],
880→        generation: Int
881→    ) async {
882→        guard !initialShares.isEmpty else { return }
883→
884→        let consumedMap: [String: Bool]
885→        do {
886→            consumedMap = try await CloudKitSharingManager.shared.consumedStatusByShareVaultIds(initialShares.map(\.id))
887→        } catch {
888→            shareVaultLogger.warning("Failed to check consumed status: \(error.localizedDescription, privacy: .private)")
889→            return
890→        }
891→        let consumedIds = Set(consumedMap.compactMap { $0.value ? $0.key : nil })
892→        guard !consumedIds.isEmpty else { return }
893→
894→        do {
895→            let updatedShares = try await Task.detached(priority: .utility) { () -> [VaultStorage.ShareRecord] in
896→                var index = try VaultStorage.shared.loadIndex(with: vaultKey)
897→                var shares = index.activeShares ?? []
898→                shares.removeAll { consumedIds.contains($0.id) }
899→                // Sort by creation date, newest first (descending order)
900→                shares.sort { $0.createdAt > $1.createdAt }
901→                index.activeShares = shares.isEmpty ? nil : shares
902→                try VaultStorage.shared.saveIndex(index, with: vaultKey)
903→                return shares
904→            }.value
905→
906→            await MainActor.run {
907→                guard generation == initializationGeneration else { return }
908→                activeShares = updatedShares
909→                updateModeForCurrentData()
910→            }
911→        } catch {
912→            shareVaultLogger.error("Failed to reconcile consumed shares: \(error.localizedDescription, privacy: .public)")
913→        }
914→    }
