106→                    // Check if we should retry (network errors or vault not found could be temporary)
107→                    let shouldRetry: Bool
108→                    switch error {
109→                    case .networkError, .vaultNotFound:
110→                        shouldRetry = attempt < retryDelays.count
111→                    default:
112→                        shouldRetry = false
113→                    }
114→                    
115→                    if shouldRetry {
116→                        try? await Task.sleep(for: .seconds(Double(delay)))
117→                        continue
118→                    } else {
119→                        mode = .error(error.localizedDescription)
120→                        return
121→                    }
122→                }
123→            }
124→            
125→            // If we get here, all retries failed
126→            if let error = lastError {
127→                mode = .error(error.localizedDescription)
128→            } else {
129→                mode = .error("Could not verify share availability. Please check your connection and try again.")
130→            }
