317→    private func handlePatternComplete(_ pattern: [Int]) {
318→        switch patternStep {
319→        case .create:
320→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
321→            validationResult = result
322→
323→            if result.isValid {
324→                errorMessage = nil
325→                newPattern = pattern
326→                patternStep = .confirm
327→                patternState.reset()
328→            } else {
329→                patternState.reset()
330→            }
331→
332→        case .confirm:
333→            if pattern == newPattern {
334→                guard !isSettingUpVault else { return }
335→                isSettingUpVault = true
336→                patternState.reset()
337→                Task { await setupSharedVault() }
338→            } else {
339→                errorMessage = "Patterns don't match. Try again."
340→                patternState.reset()
341→                Task {
342→                    try? await Task.sleep(nanoseconds: 2_500_000_000)
343→                    await MainActor.run { errorMessage = nil }
344→                }
345→            }
346→        }
347→    }
