349→    private func setupSharedVault(forceOverwrite: Bool = false, precomputedPatternKey: VaultKey? = nil) async {
350→        let trimmedPhrase = phrase.trimmingCharacters(in: .whitespacesAndNewlines)
351→        guard !trimmedPhrase.isEmpty else {
352→            mode = .error("Invalid share link — no phrase found.")
353→            return
354→        }
355→
356→        do {
357→            let patternKey = try await resolvePatternKey(precomputedPatternKey: precomputedPatternKey)
358→
359→            if VaultStorage.shared.vaultHasFiles(for: patternKey), !forceOverwrite {
360→                isSettingUpVault = false  // Reset so overwrite confirmation can proceed
361→                prepareOverwriteConfirmation(for: patternKey)
362→                return
363→            }
364→
365→            if forceOverwrite {
366→                try await overwriteExistingVaultIfNeeded(patternKey: patternKey)
367→            }
368→
369→            appState.currentVaultKey = patternKey
370→            appState.currentPattern = newPattern
371→            let letters = GridLetterManager.shared.vaultName(for: newPattern)
372→            appState.updateVaultName(letters.isEmpty ? "Vault" : "Vault \(letters)")
373→
374→            // Ensure a proper v3 vault index with encrypted master key + blob metadata.
375→            // Using loadIndex here keeps shared-invite setup aligned with JoinVaultView.
376→            let emptyIndex = try VaultStorage.shared.loadIndex(with: patternKey)
377→            try VaultStorage.shared.saveIndex(emptyIndex, with: patternKey)
378→
379→            appState.isUnlocked = true
380→            deepLinkHandler.clearPending()
381→            dismiss()
382→
383→            ShareImportManager.shared.startBackgroundDownloadAndImport(
384→                phrase: trimmedPhrase,
385→                patternKey: patternKey
386→            )
387→        } catch {
388→            mode = .error("Failed to set up vault: \(error.localizedDescription)")
389→        }
390→    }
391→
392→    private func resolvePatternKey(precomputedPatternKey: VaultKey?) async throws -> VaultKey {
393→        if let precomputedPatternKey {
394→            return precomputedPatternKey
395→        }
396→        let keyData = try await KeyDerivation.deriveKey(from: newPattern, gridSize: 5)
397→        return VaultKey(keyData)
398→    }
399→
400→    private func prepareOverwriteConfirmation(for patternKey: VaultKey) {
401→        let letters = GridLetterManager.shared.vaultName(for: newPattern)
402→        existingVaultNameForOverwrite = letters.isEmpty ? "Vault" : "Vault \(letters)"
403→
404→        if let index = try? VaultStorage.shared.loadIndex(with: patternKey) {
405→            existingFileCountForOverwrite = index.files.filter { !$0.isDeleted }.count
406→        } else {
407→            existingFileCountForOverwrite = 0
408→        }
409→
410→        pendingOverwriteKey = patternKey
411→        showingOverwriteConfirmation = true
412→    }
413→
414→    private func overwriteExistingVaultIfNeeded(patternKey: VaultKey) async throws {
415→        if VaultStorage.shared.vaultExists(for: patternKey) {
416→            if await DuressHandler.shared.isDuressKey(patternKey.rawBytes) {
417→                await DuressHandler.shared.clearDuressVault()
418→            }
419→            try VaultStorage.shared.deleteVaultIndex(for: patternKey)
420→            try? await RecoveryPhraseManager.shared.deleteRecoveryData(for: patternKey.rawBytes)
421→        }
422→    }
423→}
