467→    func performPhotoImport(_ results: [PHPickerResult]) {
468→        vmLogger.info("performPhotoImport START with \(results.count) photos")
469→        guard let key = appState?.currentVaultKey else {
470→            vmLogger.error("performPhotoImport: no vault key")
471→            return
472→        }
473→        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
474→        let count = results.count
475→        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
476→
477→        activeImportTask?.cancel()
478→        importGeneration += 1
479→        let myGeneration = importGeneration
480→        importProgress = (0, count)
481→        IdleTimerManager.shared.disable()
482→
483→        // Separate videos and images for prioritized parallel processing:
484→        // 1 dedicated video worker + 2 image workers = 3 parallel workers
485→        var videoWork: [ParallelImporter.PickerWorkItem] = []
486→        var imageWork: [ParallelImporter.PickerWorkItem] = []
487→
488→        for (index, result) in results.enumerated() {
489→            let isVideo = result.itemProvider.hasItemConformingToTypeIdentifier(UTType.movie.identifier)
490→            let item = ParallelImporter.PickerWorkItem(originalIndex: index, provider: result.itemProvider)
491→            if isVideo { videoWork.append(item) } else { imageWork.append(item) }
492→        }
493→
494→        let videoWorkerCount = videoWork.isEmpty ? 0 : 1
495→        let imageWorkerCount = videoWork.isEmpty ? 3 : 2
496→        vmLogger.info("Import: \(videoWork.count) videos, \(imageWork.count) images — \(videoWorkerCount) video + \(imageWorkerCount) image workers")
497→
498→        let (stream, continuation) = AsyncStream<ParallelImporter.ImportEvent>.makeStream()
499→
500→        activeImportTask = Task(priority: .userInitiated) { [weak self] in
501→            guard let self else { return }
502→
503→            // Launch parallel workers off MainActor
504→            let workerTask = Task.detached(priority: .userInitiated) {
505→                await ParallelImporter.runPhotoImport(
506→                    videoWork: videoWork,
507→                    imageWork: imageWork,
508→                    videoWorkerCount: videoWorkerCount,
509→                    imageWorkerCount: imageWorkerCount,
510→                    config: .init(key: key, encryptionKey: encryptionKey, optimizationMode: optimizationMode),
511→                    continuation: continuation
512→                )
513→                continuation.finish()
514→            }
515→
516→            // Consume events on MainActor for real-time UI updates
517→            var successCount = 0
518→            var failedCount = 0
519→            var lastErrorReason: String?
520→
521→            for await event in stream {
522→                guard !Task.isCancelled else { break }
523→
524→                if case .imported(let file) = event {
525→                    successCount += 1
526→                    self.files.append(file)
527→                } else if case .failed(let reason) = event {
528→                    failedCount += 1
529→                    if let reason { lastErrorReason = reason }
530→                }
531→                self.importProgress = (successCount + failedCount, count)
532→            }
533→
534→            // Cancel workers if we were cancelled
535→            if Task.isCancelled {
536→                workerTask.cancel()
537→                vmLogger.warning("Photo import cancelled (imported=\(successCount), failed=\(failedCount))")
538→            }
539→
540→            // Only clean up state if we're still the current import — a newer import
541→            // may have already set its own importProgress and disabled IdleTimerManager
542→            guard self.importGeneration == myGeneration else { return }
543→
544→            self.importProgress = nil
545→            IdleTimerManager.shared.enable()
546→
547→            guard !Task.isCancelled else { return }
548→
549→            vmLogger.info("Import complete — success: \(successCount), failed: \(failedCount), files.count: \(self.files.count)")
550→
551→            if failedCount > 0 && successCount == 0 {
552→                self.toastMessage = .importFailed(failedCount, imported: 0, reason: lastErrorReason)
553→            } else if failedCount > 0 {
554→                self.toastMessage = .importFailed(failedCount, imported: successCount, reason: lastErrorReason)
555→            } else if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count) {
556→                self.toastMessage = .milestone(milestone)
557→            } else {
558→                self.toastMessage = .filesImported(successCount)
559→            }
560→
561→            if successCount > 0 {
562→                self.updateFilterAfterImport()
563→                ShareSyncManager.shared.scheduleSync(vaultKey: key)
564→            }
565→        }
566→    }
