                existingVaultNameForOverwrite = "Vault TEST"
                existingFileCountForOverwrite = 2
                let seed = Data("MAESTRO_JOIN_OVERWRITE_TEST_KEY".utf8)
                pendingOverwriteKey = VaultKey(Data(SHA256.hash(data: seed)))
