  1→import SwiftUI
  2→import UIKit
  3→
  4→struct FilesGridView: View {
  5→    let files: [VaultFileItem]
  6→    let onSelect: (VaultFileItem) -> Void
  7→    var onDelete: ((UUID) -> Void)?
  8→    var masterKey: Data? = nil
  9→    var isEditing: Bool = false
 10→    var selectedIds: Set<UUID> = []
 11→    var onToggleSelect: ((UUID) -> Void)?
 12→
 13→    private let columns = Array(repeating: GridItem(.flexible(), spacing: 12), count: 3)
 14→
 15→    // Drag-to-select state
 16→    @State private var cellFrames: [UUID: CGRect] = [:]
 17→    @State private var isDragSelecting = true
 18→    @State private var isDragging = false
 19→    @State private var dragStartIndex: Int?
 20→    @State private var currentDragTargetIds: Set<UUID> = []
 21→    @State private var dragToggledIds: Set<UUID> = []
 22→
 23→    var body: some View {
 24→        grid
 25→            .padding(.horizontal, 16)
 26→            .coordinateSpace(name: "filesGrid")
 27→            .onPreferenceChange(FileCellFramePreference.self) { cellFrames = $0 }
 28→            .onChange(of: isEditing) { _, editing in
 29→                if !editing {
 30→                    cellFrames.removeAll()
 31→                    isDragging = false
 32→                    dragStartIndex = nil
 33→                    currentDragTargetIds.removeAll()
 34→                    dragToggledIds.removeAll()
 35→                }
 36→            }
 37→    }
 38→
 39→    @ViewBuilder
 40→    private var grid: some View {
 41→        let base = LazyVGrid(columns: columns, spacing: 12) {
 42→            ForEach(files) { file in
 43→                cellView(for: file)
 44→                    .background {
 45→                        if isEditing {
 46→                            GeometryReader { geo in
 47→                                Color.clear.preference(
 48→                                    key: FileCellFramePreference.self,
 49→                                    value: [file.id: geo.frame(in: .named("filesGrid"))]
 50→                                )
 51→                            }
 52→                        }
 53→                    }
 54→            }
 55→        }
 56→        if isEditing {
 57→            base.simultaneousGesture(dragSelectGesture)
 58→        } else {
 59→            base
 60→        }
 61→    }
 62→
 63→    @ViewBuilder
 64→    private func cellView(for file: VaultFileItem) -> some View {
 65→        VStack(spacing: 8) {
 66→            thumbnailOrIcon(for: file)
 67→                .aspectRatio(1, contentMode: .fit)
 68→                .clipShape(RoundedRectangle(cornerRadius: 8))
 69→                .overlay(alignment: .bottomTrailing) {
 70→                    if isEditing {
 71→                        selectionCircle(selected: selectedIds.contains(file.id))
 72→                            .padding(6)
 73→                    }
 74→                }
 75→
 76→            VStack(spacing: 2) {
 77→                Text(file.filename ?? "File")
 78→                    .font(.caption)
 79→                    .foregroundStyle(.vaultText)
 80→                    .lineLimit(2)
 81→                    .multilineTextAlignment(.center)
 82→
 83→                Text(formatSize(file.size))
 84→                    .font(.caption2)
 85→                    .foregroundStyle(.vaultSecondaryText)
 86→            }
 87→        }
 88→        .contentShape(Rectangle())
 89→        .onTapGesture {
 90→            if isEditing {
 91→                onToggleSelect?(file.id)
 92→            } else {
 93→                onSelect(file)
 94→            }
 95→        }
 96→        .accessibilityLabel("\(file.filename ?? "File"), \(formatSize(file.size))")
 97→        .accessibilityHint(isEditing ? "Double tap to \(selectedIds.contains(file.id) ? "deselect" : "select")" : "Double tap to open")
 98→        .contextMenu {
 99→            if let onDelete, !isEditing {
100→                Button(role: .destructive, action: { onDelete(file.id) }) {
101→                    Label("Delete", systemImage: "trash")
102→                }
103→            }
104→        }
105→    }
106→
107→    @ViewBuilder
108→    private func thumbnailOrIcon(for file: VaultFileItem) -> some View {
109→        if let masterKey, file.hasThumbnail {
110→            Color.clear
111→                .overlay {
112→                    AsyncThumbnailView(
113→                        fileId: file.id,
114→                        hasThumbnail: file.hasThumbnail,
115→                        masterKey: masterKey,
116→                        contentMode: .fill
117→                    )
118→                }
119→                .clipped()
120→                .overlay(alignment: .bottomLeading) {
121→                    if (file.mimeType ?? "").hasPrefix("video/") {
122→                        Image(systemName: "play.fill")
123→                            .font(.caption2)
124→                            .foregroundStyle(.white)
125→                            .padding(4)
126→                            .background(.black.opacity(0.6), in: RoundedRectangle(cornerRadius: 4))
127→                            .padding(4)
128→                    }
129→                }
130→        } else {
131→            Color.vaultSurface
132→                .overlay {
133→                    Image(systemName: iconName(for: file.mimeType))
134→                        .font(.title)
135→                        .foregroundStyle(.vaultSecondaryText)
136→                }
137→        }
138→    }
139→
140→    // MARK: - Drag-to-Select
141→
142→    private var dragSelectGesture: some Gesture {
143→        DragGesture(minimumDistance: 20, coordinateSpace: .named("filesGrid"))
144→            .onChanged { value in
145→                guard isEditing else { return }
146→
147→                // Setup on first callback
148→                if !isDragging {
149→                    isDragging = true
150→                    dragStartIndex = nearestItemIndex(to: value.startLocation)
151→
152→                    if let startIdx = dragStartIndex {
153→                        isDragSelecting = !selectedIds.contains(files[startIdx].id)
154→                    }
155→                }
156→
157→                guard let startIdx = dragStartIndex, startIdx < files.count else { return }
158→                guard let currentIdx = nearestItemIndex(to: value.location) else { return }
159→
160→                // Always use row-span selection (Photos-app style)
161→                let indices = DragRowSelector.indicesInRowSpan(
162→                    itemCount: files.count,
163→                    columns: columns.count,
164→                    startIndex: startIdx,
165→                    endIndex: currentIdx
166→                )
167→                let newTargetIds = Set(indices.map { files[$0].id })
168→
169→                var changed = false
170→
171→                // Items entering the drag range
172→                for id in newTargetIds.subtracting(currentDragTargetIds) {
173→                    let isSelected = selectedIds.contains(id)
174→                    if (isDragSelecting && !isSelected) || (!isDragSelecting && isSelected) {
175→                        onToggleSelect?(id)
176→                        dragToggledIds.insert(id)
177→                        changed = true
178→                    }
179→                }
180→
181→                // Items leaving the drag range (user dragged back)
182→                for id in currentDragTargetIds.subtracting(newTargetIds) {
183→                    if dragToggledIds.contains(id) {
184→                        onToggleSelect?(id)
185→                        dragToggledIds.remove(id)
186→                        changed = true
187→                    }
188→                }
189→
190→                currentDragTargetIds = newTargetIds
191→
192→                if changed {
193→                    UISelectionFeedbackGenerator().selectionChanged()
194→                }
195→            }
196→            .onEnded { _ in
197→                isDragging = false
198→                dragStartIndex = nil
199→                currentDragTargetIds.removeAll()
200→                dragToggledIds.removeAll()
201→            }
202→    }
203→
204→    /// Finds the index in `files` of the cell nearest to `point` by distance to frame center.
205→    private func nearestItemIndex(to point: CGPoint) -> Int? {
206→        var bestIndex: Int?
207→        var bestDist = CGFloat.infinity
208→        for (i, file) in files.enumerated() {
209→            guard let frame = cellFrames[file.id] else { continue }
210→            let dist = hypot(frame.midX - point.x, frame.midY - point.y)
211→            if dist < bestDist {
212→                bestDist = dist
213→                bestIndex = i
214→            }
215→        }
216→        return bestIndex
217→    }
218→
219→    // MARK: - Selection Circle
220→
221→    @ViewBuilder
222→    private func selectionCircle(selected: Bool) -> some View {
223→        ZStack {
224→            Circle()
225→                .fill(selected ? Color.accentColor : Color.black.opacity(0.3))
226→                .frame(width: 24, height: 24)
227→            Circle()
228→                .stroke(Color.white, lineWidth: 1.5)
229→                .frame(width: 24, height: 24)
230→            if selected {
231→                Image(systemName: "checkmark")
232→                    .font(.system(size: 12, weight: .bold))
233→                    .foregroundStyle(.white)
234→            }
235→        }
236→    }
237→
238→    // MARK: - Helpers
239→
240→    private func iconName(for mimeType: String?) -> String {
241→        guard let mimeType else { return "doc.fill" }
242→        if mimeType.hasPrefix("image/") { return "photo.fill" }
243→        if mimeType.hasPrefix("video/") { return "video.fill" }
244→        if mimeType.hasPrefix("application/pdf") { return "doc.text.fill" }
245→        if mimeType.hasPrefix("text/") { return "doc.text.fill" }
246→        return "doc.fill"
247→    }
248→
249→    private static let byteCountFormatter: ByteCountFormatter = {
250→        let formatter = ByteCountFormatter()
251→        formatter.countStyle = .file
252→        return formatter
253→    }()
254→
255→    private func formatSize(_ bytes: Int) -> String {
256→        Self.byteCountFormatter.string(fromByteCount: Int64(bytes))
257→    }
258→}
259→
260→// MARK: - Preference Key
261→
