151→                let tempURL = tempDir.appendingPathComponent(filename)
152→                try FileManager.default.moveItem(at: decryptedURL, to: tempURL)
153→
154→                await MainActor.run {
155→                    self.tempFileURL = tempURL
156→                    self.isLoading = false
157→                }
158→            } catch {
159→                await MainActor.run {
160→                    self.error = "Failed to decrypt file"
161→                    self.isLoading = false
162→                }
163→            }
164→        }
165→    }
