// MARK: - Share Sheet Helper

/// Presents UIActivityViewController imperatively from the topmost view controller.
/// Avoids _UIReparentingView warnings caused by UIViewControllerRepresentable inside .sheet().
enum ShareSheetHelper {
    @MainActor
    static func present(items: [Any], completion: (() -> Void)? = nil) {
        let scenes = UIApplication.shared.connectedScenes
            .compactMap { $0 as? UIWindowScene }
        let scene = scenes.first { $0.activationState == .foregroundActive } ?? scenes.first
        guard let scene else {
            completion?()
            return
        }

        let window = scene.windows.first(where: \.isKeyWindow)
            ?? scene.windows.first(where: { !$0.isHidden && $0.alpha > 0 })
        guard let root = window?.rootViewController else {
            completion?()
            return
        }

        let presenter = topPresenter(from: root)

        let activityVC = UIActivityViewController(activityItems: items, applicationActivities: nil)
        activityVC.completionWithItemsHandler = { _, _, _, _ in
            completion?()
        }
        presenter.present(activityVC, animated: true)
    }

    @MainActor
    private static func topPresenter(from root: UIViewController) -> UIViewController {
        if let presented = root.presentedViewController {
            return topPresenter(from: presented)
        }

        if let nav = root as? UINavigationController, let visible = nav.visibleViewController {
            return topPresenter(from: visible)
        }

        if let tab = root as? UITabBarController, let selected = tab.selectedViewController {
            return topPresenter(from: selected)
        }

        return root
    }
}

import UIKit
import ImageIO

enum FileUtilities {
    static func generateThumbnail(from data: Data, maxSize: CGFloat = 200) -> Data? {
        guard let image = UIImage(data: data) else { return nil }

        let size = image.size
        let scale = min(maxSize / size.width, maxSize / size.height)
        let newSize = CGSize(width: size.width * scale, height: size.height * scale)

        // Use UIGraphicsImageRenderer which properly handles orientation
        let renderer = UIGraphicsImageRenderer(size: newSize)
        let thumbnail = renderer.image { _ in
            // Draw the UIImage (not CGImage) - this automatically applies orientation
            image.draw(in: CGRect(origin: .zero, size: newSize))
        }

        return thumbnail.jpegData(compressionQuality: 0.7)
    }

    /// Memory-efficient thumbnail generation directly from file URL.
    /// Uses ImageIO downsampling to avoid decoding full-size images into memory.
    static func generateThumbnail(fromFileURL fileURL: URL, maxPixelSize: CGFloat = 400) -> Data? {
        guard let source = CGImageSourceCreateWithURL(fileURL as CFURL, nil) else { return nil }

        let options: [CFString: Any] = [
            kCGImageSourceCreateThumbnailFromImageAlways: true,
            kCGImageSourceCreateThumbnailWithTransform: true,
            kCGImageSourceThumbnailMaxPixelSize: maxPixelSize
        ]
        guard let cgImage = CGImageSourceCreateThumbnailAtIndex(source, 0, options as CFDictionary) else {
            return nil
        }
        // kCGImageSourceCreateThumbnailWithTransform already rotates pixels to correct
        // orientation, so use .up to avoid applying the EXIF rotation a second time.
        let image = UIImage(cgImage: cgImage, scale: 1.0, orientation: .up)
        return image.jpegData(compressionQuality: 0.7)
    }

    static func mimeType(forExtension ext: String) -> String {
        switch ext.lowercased() {
        case "jpg", "jpeg": return "image/jpeg"
        case "png": return "image/png"
        case "gif": return "image/gif"
        case "heic": return "image/heic"
        case "mp4": return "video/mp4"
        case "mov": return "video/quicktime"
        case "pdf": return "application/pdf"
        default: return "application/octet-stream"
        }
    }

    /// Best-effort cleanup of temporary files.
    /// Silently ignores errors - use only for non-critical temp file cleanup.
    static func cleanupTemporaryFile(at url: URL?) {
        guard let url = url else { return }
        try? FileManager.default.removeItem(at: url)
    }
}

